void subscribeToTopic(const std::string& topic) {
    mosquittopp::mosquitto* client = mosquittopp::mosquitto_new("MQTTClient", true, this);

    int rc = mosquittopp::mosquitto_subscribe(client, nullptr, topic.c_str(), 0);
    if (rc != MOSQ_ERR_SUCCESS) {
      std::cerr << "Failed to subscribe to topic: " << topic << std::endl;
      return;
    }
}
